import React from 'react';
import './App.css';
import Result from './components/result.js';
import KeyPadComponent from './components/keypad.js';

class App extends React.Component {
  // constructor(){
  //   super();
  state = {
      result:""
    }

  handleClick = button => {
    if(button === "="){
      this.calculate()
    }
    else if(button === "Clear All"){
      this.reset()
    }
    else if(button === "Clear"){
      this.backspace()
    }
    else {
      this.setState({
        result: this.state.result
      })
    }
  };

  calculate = () => {
    try{
      this.setState({
      result: (eval(this.state.result)|| "") + ""
      })
    } catch (e) {
      this.setState({
        result: "Error"
      })
    }
  }

  reset = () => {
    this.setState({
      result: ""
    })
  };

  backspace = () => {
    this.setState({
      result: this.setState.Result.slice(0, -1)
    })
  };

  render() {
    return (
      <div>
        <div className="calculator">
          <h1>calculator</h1>
          <Result result={this.state.result}/>
          <KeyPadComponent handleClick={this.handleClick}/>
        </div>

      </div>
    )
  }
}

export default App;
